    <!-- Script cho việc thay đổi số lượng và cập nhật liên kết Mua ngay -->
    <script>
        function changeQuantity(amount) {
            var quantityInput = document.getElementById('quantity');
            var currentValue = parseInt(quantityInput.value);
            if (!isNaN(currentValue)) {
                var newValue = currentValue + amount;
                if (newValue >= 1) {
                    quantityInput.value = newValue;
                    updateBuyNowLink(newValue);
                }
            }
        }

        function updateBuyNowLink(quantity) {
            var buyNowLink = document.getElementById('buy-now');
            var currentHref = buyNowLink.href;
            var newHref = currentHref.replace(/quantity=\d+/, 'quantity=' + quantity);
            buyNowLink.href = newHref;
        }
    </script>
